import { useEffect, useState } from 'react';
import { getImage } from '../utils/imageService';
import { urlFor } from '../utils/imageUrl';
import styled from 'styled-components'

const StyledSection = styled.section`
display: flex;
flex-direction: row;
justify-content: space-evenly;
`;

const StyledCaption = styled.figcaption`
text-align: center;
background: ${({ theme }) => theme.mycolors.background};
`;


const Images = () => {
  const [bilder, setBilder] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  console.log(bilder);

  useEffect(() => {
    const fetchImagesAsync = async () => {
      setLoading(true);
      try {
        const data = await getImage();
        setBilder(data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
    fetchImagesAsync();
  }, []);

  return (
    <StyledSection>
        {loading? <p>Bilder lastes..</p> : null}
         {error? <p>Noe har skjedd: {error}</p> : null}
          {bilder?.length > 0 &&
          bilder.map((images) => (
              <div>
              <img key={images.raw.image.asset._ref} src={urlFor(images?.raw?.image).width(550).format('webp').url()} alt={images.alt}/>
              <StyledCaption>{images.bildetekst}</StyledCaption>
              </div>
          ))}
    </StyledSection>
  )
};
export default Images;
